do local allowedUsers={GUNKUNGKK555=true,Gamemy214=true,TP_PORTGAS=true,Blockspinning_9=true,Cx0_r=true,BeawHunterSS=true};local Players=game:GetService("Players");local LocalPlayer=Players.LocalPlayer;if  not allowedUsers[LocalPlayer.Name] then LocalPlayer:Kick("You are not whitelisted to use this script.");return;end local Fluent=loadstring(game:HttpGet("https://github.com/dawid-scripts/Fluent/releases/latest/download/main.lua"))();local SaveManager=loadstring(game:HttpGet("https://raw.githubusercontent.com/dawid-scripts/Fluent/master/Addons/SaveManager.lua"))();local InterfaceManager=loadstring(game:HttpGet("https://raw.githubusercontent.com/dawid-scripts/Fluent/master/Addons/InterfaceManager.lua"))();local Window=Fluent:CreateWindow({Title="DarkNight",SubTitle="Hub",TabWidth=160,Size=UDim2.fromOffset(580,460),Acrylic=true,Theme="Dark",MinimizeKey=Enum.KeyCode.LeftControl});local Tabs={Main=Window:AddTab({Title="Main",Icon=""})};local Options=Fluent.Options;do Fluent:Notify({Title="Have Fun!",Content="ขอบคุณที่ใช้บริการนะครับ",SubContent="",Duration=5});local Players=game:GetService("Players");local RunService=game:GetService("RunService");local Camera=workspace.CurrentCamera;local LocalPlayer=Players.LocalPlayer;local fovRadius=50;local fovCircle;local isFOVVisible=true;local aimbotConnection;local function getCenterScreenPosition() return Vector2.new(Camera.ViewportSize.X/2 ,Camera.ViewportSize.Y/2 );end local function getClosestPlayerToCenter() local closestPlayer=nil;local shortestDistance=math.huge;local center=getCenterScreenPosition();for _,player in ipairs(Players:GetPlayers()) do if ((player~=LocalPlayer) and player.Character and player.Character:FindFirstChild("Head")) then local head=player.Character.Head;local headPos,onScreen=Camera:WorldToViewportPoint(head.Position);if onScreen then local distance=(Vector2.new(headPos.X,headPos.Y) -center).Magnitude;if ((distance<fovRadius) and (distance<shortestDistance)) then shortestDistance=distance;closestPlayer=player;end end end end return closestPlayer;end local AimbotToggle=Tabs.Main:AddToggle("AimbotToggle",{Title="ล็อคหัว ( Aimbot Head )",Default=false});AimbotToggle:OnChanged(function(Value) if Value then aimbotConnection=RunService.RenderStepped:Connect(function() local targetPlayer=getClosestPlayerToCenter();if (targetPlayer and targetPlayer.Character and targetPlayer.Character:FindFirstChild("Head")) then local headPosition=targetPlayer.Character.Head.Position;Camera.CFrame=CFrame.new(Camera.CFrame.Position,headPosition);end end);elseif aimbotConnection then aimbotConnection:Disconnect();aimbotConnection=nil;end end);Options.AimbotToggle:SetValue(false);local function createFOVCircle() if fovCircle then fovCircle:Remove();end fovCircle=Drawing.new("Circle");fovCircle.Visible=isFOVVisible;fovCircle.Radius=fovRadius;fovCircle.Position=getCenterScreenPosition();fovCircle.Thickness=2;fovCircle.Color=Color3.fromRGB(255,255,255);fovCircle.Transparency=0.5;end RunService.RenderStepped:Connect(function() if fovCircle then fovCircle.Position=getCenterScreenPosition();fovCircle.Radius=fovRadius;fovCircle.Visible=isFOVVisible;end end);createFOVCircle();local FOVToggle=Tabs.Main:AddToggle("FOV_Toggle",{Title="แสดงวง ( FOV Circle )",Default=true});FOVToggle:OnChanged(function(Value) isFOVVisible=Value;if isFOVVisible then createFOVCircle();elseif fovCircle then fovCircle.Visible=false;end end);Options.FOV_Toggle:SetValue(false);local FOVSlider=Tabs.Main:AddSlider("FOV_Radius",{Title="ปรับวง ( FOV Radius )",Description="ตั้งค่ารัศมีวง Aimbot",Default=fovRadius,Min=0,Max=200,Rounding=0,Suffix=" px",Callback=function(Value) fovRadius=Value;if isFOVVisible then createFOVCircle();end end});FOVSlider:OnChanged(function(Value) fovRadius=Value;end);FOVSlider:SetValue(50);end local Tabs={Main=Window:AddTab({Title="Misc",Icon=""})};local Options=Fluent.Options;do local Players=game:GetService("Players");local LocalPlayer=Players.LocalPlayer;local Character=LocalPlayer.Character or LocalPlayer.CharacterAdded:Wait() ;local Humanoid=Character:WaitForChild("Humanoid");local defaultJumpPower=(Humanoid.UseJumpPower and Humanoid.JumpPower) or Humanoid.JumpHeight ;local superJumpEnabled=false;local jumpPowerValue=10;local function applyJumpPower() if  not Humanoid then return;end if superJumpEnabled then if Humanoid.UseJumpPower then Humanoid.JumpPower=jumpPowerValue;else Humanoid.JumpHeight=jumpPowerValue;end elseif Humanoid.UseJumpPower then Humanoid.JumpPower=defaultJumpPower;else Humanoid.JumpHeight=defaultJumpPower;end end local Toggle=Tabs.Main:AddToggle("SuperJumpToggle",{Title="กระโดดสูง ( Super Jump )",Default=false,Callback=function(Value) superJumpEnabled=Value;applyJumpPower();end});Toggle:OnChanged(function(Value) print("Super Jump Toggled:",Value);end);Options.SuperJumpToggle:SetValue(false);local Slider=Tabs.Main:AddSlider("SuperJumpSlider",{Title="ปรับกระโดดสูง (Jump Height)",Description="ปรับระดับความสูงกระโดด",Default=jumpPowerValue,Min=0,Max=30,Rounding=1,Callback=function(Value) jumpPowerValue=Value;if superJumpEnabled then applyJumpPower();end end});Slider:OnChanged(function(Value) print("Jump Power Changed:",Value);end);Slider:SetValue(jumpPowerValue);LocalPlayer.CharacterAdded:Connect(function(char) local newHumanoid=char:WaitForChild("Humanoid");Humanoid=newHumanoid;defaultJumpPower=(newHumanoid.UseJumpPower and newHumanoid.JumpPower) or newHumanoid.JumpHeight ;if superJumpEnabled then applyJumpPower();end end);local Players=game:GetService("Players");local RunService=game:GetService("RunService");local TweenService=game:GetService("TweenService");local player=Players.LocalPlayer;local hpThreshold=30;local floatHeight=20;local flying=false;local currentTween=nil;local Toggle=Tabs.Main:AddToggle("Toggle_AntiKill",{Title="Anti Kill ( กันตาย )",Default=false});Toggle:OnChanged(function(Value) print("Toggle changed:",Options.Toggle_AntiKill.Value);if  not Value then flying=false;if currentTween then currentTween:Cancel();end end end);local function tweenToPosition(part,targetPosition) if currentTween then currentTween:Cancel();end local info=TweenInfo.new(0.15,Enum.EasingStyle.Linear,Enum.EasingDirection.InOut);currentTween=TweenService:Create(part,info,{CFrame=CFrame.new(targetPosition)});currentTween:Play();end local function startFlying(rootPart) if flying then return;end flying=true;local origin=rootPart.Position + Vector3.new(0,floatHeight,0) ;coroutine.wrap(function() while flying and rootPart and rootPart.Parent  do local randomOffset=Vector3.new(math.random( -8,8),0,math.random( -8,8));local targetPos=origin + randomOffset ;tweenToPosition(rootPart,targetPos);task.wait(0.15);end end)();end local function stopFlying() flying=false;if currentTween then currentTween:Cancel();end end local function onCharacterAdded(char) local humanoid=char:WaitForChild("Humanoid");local rootPart=char:WaitForChild("HumanoidRootPart");humanoid:GetPropertyChangedSignal("Health"):Connect(function() if  not Options.Toggle_AntiKill.Value then return;end if ((humanoid.Health>0) and (humanoid.Health<hpThreshold)) then startFlying(rootPart);elseif (flying and (humanoid.Health>=hpThreshold)) then stopFlying();end end);end if player.Character then onCharacterAdded(player.Character);end player.CharacterAdded:Connect(onCharacterAdded);end local Tabs={Main=Window:AddTab({Title="ESP",Icon=""})};local Options=Fluent.Options;do local Players=game:GetService("Players");local RunService=game:GetService("RunService");local Camera=workspace.CurrentCamera;local LocalPlayer=Players.LocalPlayer;local Settings={ESPEnabled=false,ESPName=false};local function NewLine() local line=Drawing.new("Line");line.Thickness=1;line.Transparency=1;line.Color=Color3.fromRGB(255,255,255);line.Visible=false;return line;end local function CreateNameTag() local tag=Drawing.new("Text");tag.Visible=false;tag.Size=14;tag.Center=true;tag.Outline=true;tag.OutlineColor=Color3.new(0,0,0);tag.Color=Color3.new(1,1,1);return tag;end local function CreateESP(